import React, { useEffect, useState } from 'react';
import axios from 'axios';

import classes from './Profile.module.css';
import ProfileAddress from './Components/ProfileAddress';
import ProfileEmployment from './Components/ProfileEmployment';
import ProfileFinances from './Components/ProfileFinances';
import ProfileBank from './Components/ProfileBank';
import Personal from './Components/Personal';
import { useAuth } from '../../shared/hooks/auth-hook';
import Header from '../Home/Header/Header';
import LoadingSpinner from '../../shared/components/UIElements/Spinner/LoadingSpinner';
import { baseURL } from '../../URI.js';

const URL = baseURL;
// const baseURL = 'https://kk-cash-back.herokuapp.com/api/';
// const baseURL = 'http://localhost:8000/api/';

const Profile = () => {
    const { token, userId } = useAuth();
    const [viewPersonal, setViewPerson] = useState(false);
    const [viewAddress, setViewAddress] = useState(false);
    const [viewEmployment, setViewEmployment] = useState(false);
    const [viewFinances, setViewFinances] = useState(false);
    const [viewBank, setViewBank] = useState(false);
    const [viewDone, setViewDone] = useState(false);
    const [apply, setApply] = useState(true);
    const [newApp, setNewApp] = useState(false);
    const [edit, setEdit] = useState(false);
    const [data, setData] = useState({
        loading: false,
        error: false,
        errorMessage: "",

        firstName: "",
        lastName: "",
        email: "",
        phone_number: "",

        street_name: "",
        suburb: "",
        city: "",
        province: "",
        postalcode: "",

        emp_status: "",
        gross_income: "",
        net_income: "",
        income_frequency: "",
        salary_day: "",
        work_number: "",
        university: "",
        academic_year: "",
        course_duration: "",
        division: "",
        service_time: "",
        emp_type: "",
        emplr_name: "",
        emp_industry: "",
        emp_position: "",
        time_with_employer: "",
        emp_duration: "",

        monthly_rates: "",
        groceries: "",
        commuting_costs: "",
        loan_repayments: "",
        child_maintenance: "",
        desp_income: null,

        bank_name: "",
        acc_num: "",
        acc_type: "",
        branch_code: "",
        acc_holder: "",

        addressId: "",
        empId: "",
        loanId: "",
        finId: "",
        bankId: ""
    });
    useEffect(() => {
        fetch(`${URL}user/${userId}`,
            {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`
                }
            })
            .then(response => response.json())
            .then((res) => {
                setData({
                    firstName: res.first_name,
                    lastName: res.last_name,
                    email: res.email,
                    phone_number: res.phone_number,
                });
            })
    }, [token, userId]);
    const handleInputChange = event => {
        setData({
            ...data,
            [event.target.name]: event.target.value
        });
    };

    const updatePerson = async () => {
        await axios
            .put(`${URL}user/${userId}`, {
                "first_name": data.firstName,
                "last_name": data.lastName,
                "email": data.email,
                "phone_number": data.phone_number,
            },
                {
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${token}`
                    }
                }
            )
            .then((response) => {
                setData({
                    ...data,
                    loading: false,
                });
                // viewPerson();
            })
            .catch(error => {
                setData({
                    ...data,
                    error: true,
                    errorMessage: error
                })
            });
    };
    const postAddress = async () => {
        setData({
            loading: false
        });
        await axios
            .post(`${URL}address/create/${userId}`, {
                "street": data.street_name,
                "suburb": data.suburb,
                "city": data.city,
                "province": data.province,
                "postal_code": data.postalcode
            },
                {
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${token}`
                    }
                }
            )
            .then((response) => {
                // setViewEmployment(true);
                setViewAddress(false);
                setData({
                    ...data,
                    loading: false
                });
            })
            .catch(error => {
                setData({
                    ...data,
                    error: true,
                    errorMessage: error
                })
            })
    };
    const postEmp = async () => {
        setData({
            loading: false
        });
        await axios
            .post(`${URL}employment/create/${userId}`, {
                "emp_status": data.emp_status,
                "gross_income": data.gross_income,
                "net_income": data.net_income,
