import React, { useState, useEffect } from 'react';

import Card from '../../../shared/components/UIElements/Card/Card';
import Input from '../../../shared/components/UIElements/Input/Input';

const Personal = (props) => {
    const [edit, setEdit] = useState(false);
    const [ data, setData ] = useState({});

    useEffect(() => {
        fetch(`${props.URL}user/${props.userId}`,
            {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${props.token}`
                }
            })
            .then((res) => {
                console.log(res);
                setData(JSON.parse(JSON.stringify(res)));
            })
            .then(() => {
                console.log(data);
            })
    }, [props.URL, props.token, props.userId, data]);

    return (
        <Card>
            <h2> Personal Information
                <button onClick={() => {
                    setEdit(!edit)
                }} className='btn-custom'>
                    {
                        edit
                            ? <i className='fa fa-close'></i>
                            : <i className='fa fa-edit'></i>
                    }
                </button>
            </h2>
            <Input value={data.firstName} name="firstName" placeholder="First Name" type="Text" disabled={!edit} onChange={props.onChange} />
            <Input value={data.lastName} name='lastName' placeholder='Surname' type='Text' disabled={!edit} onChange={props.onChange} />
            <Input value={data.email} name='email' placeholder='Home Language' type='email' disabled={!edit} onChange={props.onChange} />
            <Input value={data.phone_number} name='phone_number' placeholder='Cell Phone Number' type='Number' disabled={!edit} onChange={props.onChange} />
            <div>
                <div className='btns'>
                    <button onClick={props.updateReq} className='btn-custom' disabled={!edit} >Update</button>
                </div>
            </div>
        </Card >
    )
}

export default Personal;
